mapper.select:
SELECT REF
FROM QA
WHERE ID=#{id}

SELECT SEQ, ID, TITLE, CONTENT, WDATE, REF, DEL, G_CODE, SECRET FROM
(SELECT ROW_NUMBER() OVER(ORDER BY REF DESC, SEQ ASC) AS RNUM, SEQ,
ID, TITLE, CONTENT, WDATE, REF, DEL, G_CODE, SECRET FROM QA WHERE NOT
REF=-1


) A WHERE RNUM BETWEEN ${start} AND ${end}
